	private final int N = 567979;
	int[] a = new int[N + 1];
	
	Map<Long, Long> mp = new HashMap<Long, Long>();
	
	String toBitString(int x) {
		StringBuilder sb = new StringBuilder();
		for(int i = 0; i < 30; i++) {
			char c = ((x & (1 << i)) == 0) ? '0' : '1';
			sb.insert(0, c);
		}
		return sb.toString();
	}
	
	void solve() {
		a[0] = 0;
		a[1] = 1;
		for(int i = 2; i <= N; i++) {
			if(i % 2 == 0) {
				a[i] = a[i / 2];
			} else {
				a[i] = a[i / 2] + a[i / 2 + 1];
			}
		}
		
		int curMax = 0;
		for(int i = 1; i <= N; i++) {
			if(curMax < a[i]) {
				curMax = a[i];
				System.out.printf("%dL, ", i);
			}
		}
		
	}
	
	public String[] generate(int len) {
		
		String[] answer = new String[128];
		int count = 0;
		
		boolean specCase = false;
		if(len % 2 == 1) {
			specCase = true;
		}
		StringBuilder sb = new StringBuilder("10001");
		len = len - 5;
		for(int i = 0; i < len; i++) {
			if(i % 2 == 0) sb.append('0'); else sb.append('1');
		}
		sb.setCharAt(sb.length() - 1, '1');
		
		if(!specCase) {
			for(int i = 0; i < 2; i++) {
				int current = 3 - i;
				answer[count++] = sb.toString();
				while(true) {
					sb.deleteCharAt(current);
					if(current + 1 > sb.length()) {
						sb.append('1');
						break;
					}
					sb.insert(current + 1, '0');
					current += 2;
					if(current + 1 > sb.length()) break;
					answer[count++] = sb.toString();
				}
				if(i == 1) break;
			}
			sb = new StringBuilder(answer[count - 1]);
			sb.setCharAt(sb.length() - 2, '1');
			answer[count++] = sb.toString();
		} else {
			for(int i = 0; i < 2; i++) {
				int current = 3 - i;
				answer[count++] = sb.toString();
				while(true) {
					sb.deleteCharAt(current);
					if(current + 1 > sb.length()) break;
					sb.insert(current + 1, '0');
					current += 2;
					if(current + 1 > sb.length()) break;
					answer[count++] = sb.toString();
				}
				if(i == 1) break;
				sb.setCharAt(sb.length() - 2, '1');
				sb.setCharAt(sb.length() - 1, '1');
				answer[count++] = sb.toString();
			}
			
			sb = new StringBuilder(answer[1]);
			sb.setCharAt(sb.length() - 2, '1');
			sb.setCharAt(sb.length() - 3, '0');
			answer[count++] = sb.toString();
		}

		String[] response = new String[count]; 
		
		for(int i = 0; i < count; i++) {
			response[i] = answer[i];
		}
		
		return response;
	}
	
	static long toLong(String s) {
		long answer = 0;
		int d = -1;
		for(int i = s.length() - 1; i >= 0; i--) {
			d++;
			if(s.charAt(i) == '0') continue;
			answer |= (1L << d);
		}
		return answer;
	}
	
	long getValue(long ix) {
		if(ix == 0) return 0;
		if(ix == 1) return 1;
		if(mp.containsKey(ix)) return mp.get(ix);
		long value = 0;
		if(ix % 2 == 0) {
			value = getValue(ix / 2);
		} else {
			value = getValue(ix / 2) + getValue(ix / 2 + 1);
		}
		mp.put(ix, value);
		return value;
	}
	
	long[][] answerValues = new long[2][10024];
	int answerSize = 0;
	
	void addToAnswer(long index, long value) {
		answerValues[0][answerSize] = index;
		answerValues[1][answerSize] = value;
		answerSize++;
	}
	
